import { Dialog } from '@headlessui/react';
import { useForm } from 'react-hook-form'
import { Button, TextField } from '@mui/material';
import { Link } from 'react-router-dom';
import { useRef, useState } from 'react';
import { useDispatch, useSelector } from "react-redux"
import Box from '@mui/material/Box';
import React from 'react'
import { fetchAuth, selectName, logout, selectIsAuth, fetchRegistration } from '../../../redux/slices/auth';
import iconSet from "../../../styles/icons/selection.json";
import iconSet2 from "../../../styles/icons/selection2.json";
import IcomoonReact, { iconList } from "icomoon-react";


function Header() {
   const {
      register,
      handleSubmit,
      setError,
      formState: { errors, isValid },
   } = useForm({
      defaultValues: {
         email: '',
         password: '',
      }
   })
   const {
      register: register2,
      formState: { errors: errors2, isValid: isValid2 },
      handleSubmit: handleSubmit2,
   } = useForm({
      defaultValues: {
         email: '',
         password: '',
         fullName: '',
      }
   })
   const dispatch = useDispatch()
   const isAuth = useSelector(selectIsAuth)
   const name = useSelector(selectName)
   const [isOpenPopup1, setIsOpenPopup1] = useState(false)
   const [isOpenPopup2, setIsOpenPopup2] = useState(false)
   const [input1, setInput1] = useState('')

   function changeInput1() {
      // тут будет код 
   }


   const onSubmit = async (values) => {
      const data = await dispatch(fetchAuth(values))

      if (!data.payload) {
         return alert("Не удалось авторизоваться")
      }

      if ('token' in data.payload) {
         window.localStorage.setItem('token', data.payload.token)
         setIsOpenPopup1(false)
      }
   }

   const onSubmit2 = async (values) => {
      const data = await dispatch(fetchRegistration(values))

      if (!data.payload) {
         return alert("Не удалось зарегистрироваться. Скорее всего, ваш пароль слишком короткий или аккаунт с подобной электронной почтой уже существует")
      }

      if ('token' in data.payload) {
         window.localStorage.setItem('token', data.payload.token)
         setIsOpenPopup2(false)
      }
   }

   const onClickLogout = () => {
      if (window.confirm('Вы действительно хотите выйти?')) {
         dispatch(logout())
         window.localStorage.removeItem('token')
      }

   }

   class AccountIcon extends React.Component {
      render() {
         var defaultClass = '_icon-user-o auth-svg';
         if (isAuth) defaultClass += ' green-auth-svg';
         return <span className={defaultClass}></span>
      }
   }


   return (
      <div class="fullscreen__header">
         <div class="fullscreen__logo logo"><IcomoonReact className='logo__icon' iconSet={iconSet2} color="#5EA93A" size={72} icon="exchange" /><span class="logo__green"> OnlineExchange</span>
         </div>
         <div class="fullscreen__navbar navbar">
            <div className="navbar__case">
            <input
               className='navbar__input'
               type="text"
               name='input1'
               value={input1}
               onChange={(event) => setInput1(event.target.value)}
            />
            <IcomoonReact className='navbar__icon' iconSet={iconSet2} color="#5EA93A" size={35} icon="search" />
            </div>
         </div>               
         {
            isAuth === true
               ? <>
                  <div class="fullscreen__account account" onClick={() => onClickLogout()}>
                     <div className='account__name'>{name}</div>
                     {/* <AccountIcon /> */}
                     <Link to={'/chat'} class="logo-link" relative="path">
                     <AccountIcon/>
                     </Link>
                  </div>
                  <Link to={'/chat'} class="logo-link" relative="path">
                     <IcomoonReact iconSet={iconSet} color="#308446" size={30} icon="bubble1" />
                  </Link>
               </>
               : <><div class="fullscreen__account account" onClick={() => setIsOpenPopup1(true)}><IcomoonReact iconSet={iconSet2} color="#5EA93A" size={40} icon="enter" /></div></>
         }
         <div className="fullscreen__line"></div>
         <div class="fullscreen__burger">
            <span></span>
         </div>
      </div>
   )
}
export default Header
