import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "../../axios";

export const createMessage = createAsyncThunk('chat/createMessage', async (params) => {
   console.log("fetch")
   const { data } = await axios.post('/messages/create', params)
   return data;
})
export const fetchMessages = createAsyncThunk('chat/fetchMessage', async (params) => {
   console.log("fetch")
   const { data } = await axios.get('/messages/get')
   return data;
})

const initialState = {
   messages: {},
   status: 'loading'
}


const chatSlice = createSlice({
   name: 'chat',
   initialState,
   reducers: { 
   },
   extraReducers: {
      [fetchMessage.pending]: (state) => {
         state.status = 'loading';
         state.messages = null;
      },
      [createMessage.fulfilled]: (state, action) => {
         state.status = 'loaded';
         state.masseges = action.payload;
      },
      [createMessage.rejected]: (state) => {
         state.status = 'error';
         state.messages = null;
      },
   }
});

// export const selectIsAuth = (state) => Boolean(state.auth.data)
// export const selectName = (state) => state.auth.data?.fullName

export const chatReducer = chatSlice.reducer

